INSERT INTO `wp_posts` VALUES ('41', '1', '2016-12-19 16:57:17', '2016-12-19 16:57:17', '<div class=\"feature-products\">\r\n<div class=\"container\">[wpcs id=\'65\']</div>\r\n</div>\r\n<div class=\"tw-choice-us\">\r\n<div class=\"container\">\r\n<div class=\"row\">\r\n<div class=\"col-sm-4\">\r\n<div class=\"wrap\">\r\n<div class=\"icon\"><i class=\"fa fa-diamond\">&nbsp;</i></div>\r\n<h3>Phương châm hoạt động</h3>\r\n<p class=\"content\">Với phương châm uy tín- chất lượng, Công ty Thảo Nguyên đã nhập khẩu nguyên liệu đầu vào từ những nơi uy tín như Mỹ, Ấn Độ. Công ty Thảo Nguyên đã duy trì và đang phát bền vững thương hiệu Thaonguyentex trong lòng khách hang đối với tất cả các loại khăn bông theo đặt hàng nội địa và xuất khẩu...</p>\r\n<a class=\"read-more\">Xem thêm</a>\r\n</div>\r\n</div>\r\n<div class=\"col-sm-4\">\r\n<div class=\"wrap\">\r\n<div class=\"icon\"><i class=\"fa fa-usd\">&nbsp;</i></div>\r\n<h3>Cam kết sản phẩm</h3>\r\n<p class=\"content\">Thảo Nguyên xin cam kết sản phẩm được dệt từ sợi bông tự nhiên ( 100% cotton ) có bề mặt mềm mịn và độ thấm hút nước cao tạo cảm giác dễ chịu và an toàn, thân thiện với môi trường và người sử dụng ...</p>\r\n<a class=\"read-more\">Xem thêm</a>\r\n</div>\r\n</div>\r\n<div class=\"col-sm-4\">\r\n<div class=\"wrap\">\r\n<div class=\"icon\"><i class=\"fa fa-plane\">&nbsp;</i></div>\r\n<h3>Vận chuyển - Thanh toán</h3>\r\n<p class=\"content\">Chúng tôi xin cam kết vận chuyển sản phẩm tới khách hàng một cách nhanh chóng nhất dù bạn có ở bất cứ đâu trong và ngoài nước. Thanh toán nhanh gọn và bảo mật ...</p>\r\n<a class=\"read-more\">Xem thêm</a>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', 'Trang chủ', '', 'publish', 'closed', 'closed', '', 'trang-chu', '', '', '2017-02-20 14:17:18', '2017-02-20 14:17:18', '', '0', 'http://localhost/wp/?page_id=41', '0', 'page', '', '0');
