INSERT INTO `wp_term_taxonomy` VALUES ('10', '10', 'product_cat', 'Khăn Thể Thao Thảo Nguyên được thiết kế giành riêng cho những ai yêu thích thể thao như cầu lông, bơi lội, tennis hay chạy bộ. Thiết kế đẹp cùng với chất liệu, màu sắc phù hợp với từng đối tượng cụ thể. \r\nThiết kế đẹp cùng với chất liệu, màu sắc phù hợp với từng đối tượng.\r\nCụ thể, chất liệu cotton 100%  giúp thấm hút mồ hôi nhanh chóng và là đặc điểm nổi bật của dòng sản phẩm này.', '0', '10');
INSERT INTO `wp_term_taxonomy` VALUES ('11', '11', 'product_cat', '\"Khăn lau  kính được làm từ sợi bông, sợi se đơn mềm mại, nhẹ, thấm hút tốt.\r\n\r\n- An toàn khi sử dụng, không gây kích ứng cho da.\r\n\r\n- Thiết kế 2 mặt trang nhã, viền cẩn thận.\r\n\r\n- Tiện lợi bạn có thể mang theo mọi lúc, mọi nơi khi đi chơi xa, đi công tác…\r\n\r\n- Màu sắc tươi sáng, bền đẹp.\"\r\n', '0', '0');
