INSERT INTO `wp_term_taxonomy` VALUES ('14', '14', 'product_cat', 'Khăn bếp, chuyên dụng cho việc vệ sinh trong căn bếp của các bà nội trợ, có 5 màu, nhuộm màu hoàn nguyên rất đẹp, 100% cotton, sợi CD, mềm, độ thấm nước cao, không ra lông khi dùng. \r\nSản phẩm được dò kim rất kỹ trước khi xuất khẩu, đóng gói theo nhiều tiêu quy cách khác nhau.								\r\n', '0', '0');
