INSERT INTO `wp_term_taxonomy` VALUES ('19', '19', 'product_cat', 'Một món quà tặng đầy ý nghĩa gửi những thông điệp yêu thương sẽ là sợi dây kết nối giữa bạn bè và người thân trong những dịp đặc biệt. \r\nVới khăn Thảo Nguyên, bạn có thể tự do sáng tạo và lựa chọn các sản phẩm khác nhau để có một món quà như ý. \r\nThảo Nguyên luôn mong muốn sẽ mang bạn lại gần hơn nữa với những người thân yêu.\r\n', '0', '10');
INSERT INTO `wp_term_taxonomy` VALUES ('20', '20', 'product_cat', 'Với phương châm \"luôn luôn thấu hiểu, luôn luôn sẻ chia\", Thảo Nguyên luôn mong muốn mang đến hài lòng cũng như đẳng cấp cho khách hàng. \r\nChính vì vậy, dòng sản phẩm khăn khách sạn &amp; spa của Thảo Nguyên được thiết kế riêng với các đường nét nhẹ nhàng, tinh tế và sang trọng, góp phần tạo nên một không gian hiện đại nhưng vẫn ấm cúng, gần gũi và thoải mái.\r\n', '0', '10');
